        public void CalcDisplayFreq()                           // changes yt7pwr
        {
            double zoom_factor = 0.0;
            double pan_factor = 0.0;
            double vfo = 0.0;
            int abs_low = (int)(-RXsample_rate / 2);
            int abs_high = -abs_low;
            double correction = 0.0;
            int low = 0, high = 0, low_tmp = 0, high_tmp = 0;

            if (current_display_mode == DisplayMode.SPECTRUM)
            {
                UpdateRXDisplayVars((int)udFilterLow.Value, (int)udFilterHigh.Value);
            }
            else
            {
                if (MOX)
                {
                    zoom_factor = ptbDisplayZoom.Value / 4;
                    pan_factor = ptbDisplayPan.Value;
                    //vfo = LOSCFreq * 1e6 - DttSP.TXFilterHighCut / 2;
                    correction = 2 * (pan_factor * abs_high) / ptbDisplayPan.Maximum;
                    low = (int)(abs_low / zoom_factor);
                    high -= low;
                }

                switch (current_click_tune_mode)
                {
                    case ClickTuneMode.VFOA:
                    case ClickTuneMode.Off:
                        {
                            if (!MOX)
                            {
                                zoom_factor = ptbDisplayZoom.Value / 4;
                                pan_factor = ptbDisplayPan.Value;
                                vfo = -((LOSCFreq - VFOAFreq) * 1e6);
                                correction = 2 * (pan_factor * abs_high) / ptbDisplayPan.Maximum;
                                low = (int)(-RXsample_rate / (zoom_factor * 2));
                                high = (int)(RXsample_rate / (zoom_factor * 2));

                                if (ptbDisplayZoom.Value == 4)
                                {
                                    ptbDisplayPan.Value = 0;
                                    low = (int)(-RXsample_rate / 2);
                                    high = -low;
                                }
                                else
                                {
                                    if (LOSCFreq < VFOAFreq)
                                    {
                                        switch (CurrentDSPMode)
                                        {
                                            case (DSPMode.LSB):
                                            case (DSPMode.DIGL):
                                                vfo += DttSP.RXFilterLowCut / 2;
                                                break;
                                            case (DSPMode.USB):
                                            case (DSPMode.DIGU):
                                                vfo += DttSP.RXFilterHighCut / 2;
                                                break;
                                        }

                                        high_tmp = ((int)vfo + high + (int)correction);

                                        if (high_tmp > abs_high)
                                        {
                                            high = abs_high;
                                            low = (abs_high + low * 2);
                                        }
                                        else
                                        {
                                            low = (high_tmp + low * 2);
                                            if (low < abs_low)
                                            {
                                                low = abs_low;
                                                high = low + high * 2;
                                            }
                                            else
                                                high = high_tmp;
                                        }
                                    }
                                    else
                                    {
                                        if (LOSCFreq > VFOAFreq)
                                        {
                                            switch (CurrentDSPMode)
                                            {
                                                case (DSPMode.LSB):
                                                case (DSPMode.DIGL):
                                                    vfo += DttSP.RXFilterLowCut / 2;
                                                    break;
                                                case (DSPMode.USB):
                                                case (DSPMode.DIGU):
                                                    vfo += DttSP.RXFilterHighCut / 2;
                                                    break;
                                            }

                                            low_tmp = ((int)vfo + low + (int)correction);
                                            if (low_tmp < abs_low)
                                            {
                                                low = abs_low;
                                                high = abs_low + high * 2;
                                            }
                                            else
                                            {
                                                high = (low_tmp + high * 2);
                                                if (high > abs_high)
                                                {
                                                    high = abs_high;
                                                    low = abs_high + low * 2;
                                                }
                                                else
                                                    low = low_tmp;
                                            }
                                        }
                                    }
                                }
                            }
                        }
                        break;

                    case ClickTuneMode.VFOB:
                        {
                            if (!MOX)
                            {
                                zoom_factor = ptbDisplayZoom.Value / 4;
                                pan_factor = ptbDisplayPan.Value;
                                vfo = -((LOSCFreq - VFOBFreq) * 1e6);
                                correction = 2 * (pan_factor * abs_high) / ptbDisplayPan.Maximum;
                                low = (int)(-RXsample_rate / (zoom_factor * 2));
                                high = (int)(RXsample_rate / (zoom_factor * 2));

                                if (ptbDisplayZoom.Value == 4)
                                {
                                    ptbDisplayPan.Value = 0;
                                    low = (int)(-RXsample_rate / 2);
                                    high = -low;
                                }
                                else
                                {
                                    if (LOSCFreq < VFOBFreq)
                                    {
                                        switch (CurrentDSPMode)
                                        {
                                            case (DSPMode.LSB):
                                            case (DSPMode.DIGL):
                                                vfo += DttSP.RXFilterLowCut / 2;
                                                break;
                                            case (DSPMode.USB):
                                            case (DSPMode.DIGU):
                                                vfo += DttSP.RXFilterHighCut / 2;
                                                break;
                                        }

                                        high_tmp = ((int)vfo + high + (int)correction);
                                        if (high_tmp > abs_high)
                                        {
                                            high = abs_high;
                                            low = (abs_high + low * 2);
                                        }
                                        else
                                        {
                                            low = (high_tmp + low * 2);
                                            if (low < abs_low)
                                            {
                                                low = abs_low;
                                                high = low + high * 2;
                                            }
                                            else
                                                high = high_tmp;
                                        }
                                    }
                                    else
                                    {
                                        if (LOSCFreq > VFOBFreq)
                                        {
                                            switch (CurrentDSPMode)
                                            {
                                                case (DSPMode.LSB):
                                                case (DSPMode.DIGL):
                                                    vfo += DttSP.RXFilterLowCut / 2;
                                                    break;
                                                case (DSPMode.USB):
                                                case (DSPMode.DIGU):
                                                    vfo += DttSP.RXFilterHighCut / 2;
                                                    break;
                                            }

                                            low_tmp = ((int)vfo + low + (int)correction);
                                            if (low_tmp < abs_low)
                                            {
                                                low = abs_low;
                                                high = abs_low + high * 2;
                                            }
                                            else
                                            {
                                                high = (low_tmp + high * 2);
                                                if (high > abs_high)
                                                {
                                                    high = abs_high;
                                                    low = abs_high + low * 2;
                                                }
                                                else
                                                    low = low_tmp;
                                            }
                                        }
