                                    if (current_display_mode != DisplayMode.OFF)
                                        Display_DirectX.RenderDirectX();
                                    pause_DisplayThread = false;
                                    pause_multimeter_thread = false;
                                }
                            }
                            break;
#endif
                    }

                    pause_DisplayThread = false;
                }
                catch (Exception ex)
                {
                    Debug.Write(ex.ToString());
                    pause_DisplayThread = false;
                }
            }
        }

