                Thread.Sleep(100);
                Display_GDI.Init();
                this.picDisplay = new System.Windows.Forms.PictureBox();
                ((System.ComponentModel.ISupportInitialize)(this.picDisplay)).BeginInit();
                this.grpDisplay.Controls.Add(this.picDisplay);
