                                                sMeterDigital2.SignalValue = ((num + 30.0f) / 1.66f);
                                                break;
                                        }
                                    }
                                    else
                                        NewVFOSignalGauge.Value = ((num + 30.0f) / 1.66f);

                                    break;
                                case MeterTXMode.LEVELER:
                                    if (Audio.CurrentAudioState1 == Audio.AudioState.DTTSP)
