                                                break;
                                        }
                                    }
                                    else
                                        AnalogSignalGauge.Value = ((num + 30.0f) / 1.66f);

                                    break;
                                case MeterTXMode.LVL_G:
                                    if (Audio.CurrentAudioState1 == Audio.AudioState.DTTSP)
                                    {
                                        num = (float)Math.Max(thread_no, DttSP.CalculateTXMeter(thread_no, DttSP.MeterType.LVL_G));
