                if (!lock_TUN)
                    TunePower = ptbPWR.Value;    // save TUN power level

                ptbPWR.Value = (int)PreviousPWR;
                lblPWRValue.Text = ptbPWR.Value.ToString();

                if (chkVFOSplit.Checked &&
                    (current_dsp_mode_subRX != DSPMode.CWL || current_dsp_mode_subRX != DSPMode.CWU))
                {
                    CurrentDSPModeSubRX = pre_tune_mode;
                }
                else if (current_dsp_mode != DSPMode.CWL || current_dsp_mode != DSPMode.CWU)
                {
                    CurrentDSPMode = pre_tune_mode;
                }
            }
        }

        public void SetTXOscFreqs(bool tx, bool losc)       // yt7pwr
        {
            try
            {
                if (!vfoA_drag || !spectrum_drag)
                {
                    double freq = 0.0;
                    double tmpFreq = 0.0;

                    if (chkVFOSplit.Checked)
                    {
                        freq = vfoBFreq;
                    }
                    else
                    {
                        freq = vfoAFreq;
                    }

                    freq = Math.Round(freq, 6);

                    if (tx)
                    {                       
                        if (tx_IF)
                        {
                            if (chkXIT.Checked)
                            {
                                DttSP.SetTXOsc(0, TX_IF_shift * 1e5 + XITValue);
                                DttSP.SetTXOsc(1, TX_IF_shift * 1e5 + XITValue);
                                DttSP.SetKeyerFreq((float)(TX_IF_shift * 1e5 + XITValue));
                            }
                            else
                            {
                                DttSP.SetTXOsc(0, (TX_IF_shift * 1e5));
                                DttSP.SetTXOsc(1, (TX_IF_shift * 1e5));
                                DttSP.SetKeyerFreq((float)(TX_IF_shift * 1e5));
                            }
                        }
                        else
                        {
                            tmpFreq = (freq - LOSCFreq) * 1e6;

                            if (chkXIT.Checked)
                                tmpFreq += XITValue;

                            DttSP.SetTXOsc(0, tmpFreq);
                            DttSP.SetTXOsc(1, tmpFreq);
                            DttSP.SetKeyerFreq((float)tmpFreq);
                        }

                        DttSP.SetTXFilters(tx_filter_low, tx_filter_high);
                        DttSP.SetTXIQPhase(0, (double)tx_image_phase_table[(int)current_band]);
                        DttSP.SetTXIQPhase(1, (double)tx_image_phase_table[(int)current_band]);
                        DttSP.SetTXIQGain(0, (double)tx_image_gain_table[(int)current_band]);
                        DttSP.SetTXIQGain(1, (double)tx_image_gain_table[(int)current_band]);
                    }

                    if (losc)
                    {
                        if (tx && tx_IF)          // for fixed TX IF frequency
                        {
                            tmpFreq = (freq - (TX_IF_shift / 10));
                            tmpFreq = Math.Round(tmpFreq, 6);

                            if (current_dsp_mode == DSPMode.FMN)
                            {
                                switch (rptr_mode)
                                {
                                    case RPTRmode.simplex:
                                        break;

                                    case RPTRmode.low:
                                        tmpFreq -= RPTR_offset;
                                        break;

                                    case RPTRmode.high:
                                        tmpFreq += RPTR_offset;
                                        break;
                                }

                                tmpFreq = Math.Round(tmpFreq, 6);
                            }

                            tmpFreq *= 1e6;

                            if (current_model == Model.LimeSDR)
                            {
                                if (IsXTRV(freq))
                                {
                                    switch (current_band)
                                    {
                                        case Band.B2M:
                                            //tmpFreq -= g11_Xtrv_losc_freq;
                                            break;
                                        case Band.BX1:
                                            tmpFreq -= xBand[1].losc * 1e6;
                                            break;
                                        case Band.BX2:
                                            tmpFreq -= xBand[2].losc * 1e6;
                                            break;
                                        case Band.BX3:
                                            tmpFreq -= xBand[3].losc * 1e6;
                                            break;
                                        case Band.BX4:
                                            tmpFreq -= xBand[4].losc * 1e6;
                                            break;
                                        case Band.BX5:
                                            tmpFreq -= xBand[5].losc * 1e6;
                                            break;
                                        case Band.BX6:
                                            tmpFreq -= xBand[6].losc * 1e6;
                                            break;
                                        case Band.BX7:
                                            tmpFreq -= xBand[7].losc * 1e6;
                                            break;
                                        case Band.BX8:
                                            tmpFreq -= xBand[8].losc * 1e6;
                                            break;
                                        case Band.BX9:
                                            tmpFreq -= xBand[9].losc * 1e6;
                                            break;
                                        case Band.BX10:
                                            tmpFreq -= xBand[10].losc * 1e6;
                                            break;
                                        case Band.BX11:
                                            tmpFreq -= xBand[11].losc * 1e6;
                                            break;
                                        case Band.BX12:
                                            tmpFreq -= xBand[12].losc * 1e6;
                                            break;
                                    }
                                }

                                if (tmpFreq < 0)
                                {
                                    MessageBox.Show("Wrong OSC value!");
                                    return;
                                }

                                Band new_band_filter = BandFilterByFreq(freq);

                                if (current_band_filter != new_band_filter)
                                {
                                    CurrentBandFilter = new_band_filter;
                                    if (debug_enabled && debug != null && debug.Visible)
                                    {
                                        debug.rtbDebugMsg.AppendText("LimeSDR new band filter: " + new_band_filter.ToString() + "\n");
                                        SendMessage(debug.rtbDebugMsg.Handle, WM_VSCROLL, SB_BOTTOM, 0);
                                    }
                                }

                                if (debug_enabled && debug != null && debug.Visible)
                                {
                                    debug.rtbDebugMsg.AppendText("LimeSDR LOSC freq: " + Math.Round(tmpFreq, 6).ToString() + "\n");
                                    SendMessage(debug.rtbDebugMsg.Handle, WM_VSCROLL, SB_BOTTOM, 0);
                                }

                                if(!limeSDR.Set_RX0_LOSC((UInt32)tmpFreq) || !limeSDR.Set_TX0_LOSC((UInt32)tmpFreq))
                                    chkPower.Checked = false;

                            }
                        }
                        else if (tx)
                        {
                            freq = loscFreq;
                            freq = Math.Round(freq, 6);

                            if (current_dsp_mode == DSPMode.FMN)
                            {
                                switch (rptr_mode)
                                {
                                    case RPTRmode.simplex:
                                        break;

                                    case RPTRmode.low:
                                        freq -= RPTR_offset;
                                        break;

                                    case RPTRmode.high:
                                        freq += RPTR_offset;
                                        break;
                                }
                            }
                        }
                        else
                        {                                   // rx losc set
                            tmpFreq = loscFreq;

                            if (current_model == Model.LimeSDR)
                            {
                                tmpFreq = loscFreq * 1e6;

                                switch (current_band)
                                {
                                    case Band.BX1:
                                        tmpFreq -= xBand[1].losc * 1e6;
                                        break;
                                    case Band.BX2:
                                        tmpFreq -= xBand[2].losc * 1e6;
                                        break;
                                    case Band.BX3:
                                        tmpFreq -= xBand[3].losc * 1e6;
                                        break;
                                    case Band.BX4:
                                        tmpFreq -= xBand[4].losc * 1e6;
                                        break;
                                    case Band.BX5:
                                        tmpFreq -= xBand[5].losc * 1e6;
                                        break;
                                    case Band.BX6:
                                        tmpFreq -= xBand[6].losc * 1e6;
                                        break;
                                    case Band.BX7:
                                        tmpFreq -= xBand[7].losc * 1e6;
                                        break;
                                    case Band.BX8:
                                        tmpFreq -= xBand[8].losc * 1e6;
                                        break;
                                    case Band.BX9:
                                        tmpFreq -= xBand[9].losc * 1e6;
                                        break;
                                    case Band.BX10:
                                        tmpFreq -= xBand[10].losc * 1e6;
                                        break;
                                    case Band.BX11:
                                        tmpFreq -= xBand[11].losc * 1e6;
                                        break;
                                    case Band.BX12:
                                        tmpFreq -= xBand[12].losc * 1e6;
                                        break;
                                }

                                Math.Round(tmpFreq, 6);

                                if (tmpFreq < 0)
                                {
                                    MessageBox.Show("Wrong OSC value!");
                                    return;
                                }

                                if(!limeSDR.Set_RX0_LOSC((UInt32)(tmpFreq)) || !limeSDR.Set_TX0_LOSC((UInt32)(tmpFreq)))
                                    chkPower.Checked = false;
                            }
                        }
                    }
                }
            }
            catch (Exception ex)
            {
                Debug.Write(ex.ToString());
            }
        }

