                                if (!allow_vfo_drag)
                                {
                                    vfoa_whole_filter_drag = true;
                                    vfoa_whole_filter_start_x = e.X;

                                    if (!chkMOX.Checked)
                                    {
                                        vfoa_whole_filter_start_low = (int)udFilterLow.Value;
                                        vfoa_whole_filter_start_high = (int)udFilterHigh.Value;
