                                    else
                                    {
                                        vfoa_whole_filter_start_low = SetupForm.TXFilterLow;
                                        vfoa_whole_filter_start_high = SetupForm.TXFilterHigh;
                                    }

                                    Cursor = Cursors.NoMoveHoriz;
                                }
                                else
                                {
                                    vfoA_drag_last_x = e.X;
                                    vfoA_drag = true;
                                    vfoA_drag_start_freq = VFOAFreq;
                                    Cursor = Cursors.SizeWE;
