        private void Band160(bool save_band_stack, bool get_band_stack, bool clicked)
        {
            if (save_band_stack)
                SaveBand();

            if (last_band.Equals("160M") && save_band_stack && get_band_stack && !clicked)
            {
                if ((Control.ModifierKeys & Keys.Shift) == Keys.Shift)
