        public bool ReInit_USB()
        {
            bool result = false;

            try
            {
                if (!booting)
                {
                    switch (current_model)
                    {
                        case Model.LimeSDR:
                            {
                                btnUSB.Visible = true;

                                if (limeSDR != null && limeSDR.connected)
                                    limeSDR.Close();

                                if (!limeSDR.connected)
                                {
                                    result = limeSDR.Open();

                                    if (result)
                                        btnUSB.BackColor = Color.Green;
                                    else
                                        btnUSB.BackColor = Color.Red;
                                }
                                else
                                    btnUSB.BackColor = Color.Green;
                            }
                            break;

                        default:
                            {
                                btnUSB.BackColor = Color.Red;
                                limeSDR.connected = false;
                                btnUSB.Visible = false;
                            }
                            break;
                    }
                }

                return result;
            }
            catch (Exception ex)
            {
                Debug.Write(ex.ToString());
                return false;
            }
        }

