		#region Constructor and Destructor

        public Setup(Console c)
        {
            try
            {
                this.AutoScaleMode = AutoScaleMode.Inherit;
                InitializeComponent();
                float dpi = this.CreateGraphics().DpiX;
                float ratio = dpi / 96.0f;
                string font_name = this.Font.Name;
                float size = (float)(8.25 / ratio);
                System.Drawing.Font new_font = new System.Drawing.Font(font_name, size);
                this.Font = new_font;
                this.PerformAutoScale();
                this.PerformLayout();

                console = c;

#if(!DEBUG)
                comboGeneralProcessPriority.Items.Remove("Idle");
                comboGeneralProcessPriority.Items.Remove("Below Normal");
#endif
                initializing = false;

                settings.audio_latency1 = 50;
                settings.audio_latencyVAC = 50;
                settings.audio_line_gain = 20;
                settings.audio_mic_gain = 50;
                settings.audio_output_voltage = 1.0;

                InitWindowTypes();
                GetHosts();
                GetDevicesVAC();

                KeyList = new ArrayList();
                SetupKeyMap();
                GetTxProfiles();
                console.cat_push_data = chkCATPushData.Checked;
                RefreshCOMPortLists();
                RefreshSkinList();
                InitSMeterModes();

                radGenModelLimeSDR.Checked = true;
                comboGeneralProcessPriority.Text = "Normal";
                comboOptFilterWidthMode.Text = "Linear";
                comboRXBuffer.Text = "16384";
                comboTXBuffer.Text = "16384";
                comboAudioBufferVAC.Text = "2048";
                comboAudioSampleRateVAC.Text = "48000";
                comboTXSampleRate.Text = "768000";
                comboRXSampleRate.Text = "768000";

                comboDisplayLabelAlign.Text = "Auto";
                comboDisplayDriver.Text = "DirectX";
                comboDSPBufSizeDigital.SelectedIndex = 1;
                comboDSPBufSizeCW.SelectedIndex = 1;
                comboDSPBufSizePhone.SelectedIndex = 1;
                comboDSPBufSizeCW.Text = "32768";
                comboDSPBufSizePhone.Text = "32768";
                comboDSPBufSizeDigital.Text = "32768";
                comboDSPWindow.SelectedIndex = (int)Window.HANNING;
                comboKeyerConnDASHLine.SelectedIndex = 0;
                comboKeyerConnPTTLine.SelectedIndex = 0;
                comboKeyerConnSecondary.SelectedIndex = 0;
                comboKeyerConnDOTLine.SelectedIndex = 0;

                comboKeyerConnPrimary.SelectedIndex = 0;

                if (comboCATPort.Items.Count > 0) comboCATPort.SelectedIndex = 0;
                if (comboCATPTTPort.Items.Count > 0) comboCATPTTPort.SelectedIndex = 0;

                comboCATbaud.Text = "1200";
                comboCATparity.Text = "none";
                comboCATdatabits.Text = "8";
                comboCATstopbits.Text = "1";
                comboCATRigType.Text = "TS-2000";
                grpCATIcom.Visible = false;

                chkAudioEnableVAC_CheckedChanged(this, EventArgs.Empty);

                comboColorPalette.Text = "enhanced";
                comboAppSkin.Text = "Blue";

                GetOptions();

                cmboTestGenMode.Text = "Off";

                if (comboTXProfileName.SelectedIndex < 0 &&
                    comboTXProfileName.Items.Count > 0)
                    comboTXProfileName.SelectedIndex = 0;

                initializing = false;

                comboKeyerConnSecondary_SelectedIndexChanged(this, EventArgs.Empty);

                if (chkPrimaryDirectI_Q.Checked)
                {
                    Audio.PrimaryDirectI_Q = true;
                    Audio.RXShift = (double)udPrimaryRXshift.Value;
                    Audio.Primary_RXshift_enabled = chkPrimaryRXshiftEnabled.Checked;
                    Audio.VACDirectI_Q = false;
                }
                else if (chkVACDirectI_Q.Checked)
                {
                    Audio.VACDirectI_Q = true;
                    Audio.RXShift = (double)udRXShift.Value;
                    Audio.VAC_RXshift_enabled = chkVACRXShiftEnable.Checked;
                    Audio.PrimaryDirectI_Q = false;
                }
                else
                {
                    Audio.VACDirectI_Q = false;
                    Audio.RXShift = 0.0;
                    Audio.Primary_RXshift_enabled = false;
                    Audio.VAC_RXshift_enabled = false;
                    Audio.PrimaryDirectI_Q = false;
                }

                radGenModelLimeSDR_CheckedChanged(this, EventArgs.Empty);
                comboSpectrumSize_SelectedIndexChanged(this, EventArgs.Empty);
                udDisplayWaterfallAvgTime_ValueChanged(this, EventArgs.Empty);

                ReloadSkins();
            }
            catch (Exception ex)
            {
                MessageBox.Show("Error in Setup function!\n" + ex.ToString());
            }
        }

