        private void GetHosts()         // changes yt7pwr
        {
            try
            {
                if (console.CurrentModel == Model.LimeSDR && radGenModelLimeSDR.Checked)
                {
                    comboAudioDriverVAC.Items.Clear();
                    int host_index = 0;

                    foreach (string PAHostName in Audio.GetPAHosts())
                    {
                        if (Audio.GetPAInputdevices(host_index).Count > 0 ||
                            Audio.GetPAOutputdevices(host_index).Count > 0)
                        {
                            comboAudioDriverVAC.Items.Add(new PADeviceInfo(PAHostName, host_index));
                        }

                        host_index++; //Increment host index
                    }

                    if (comboAudioDriverVAC.Items.Count > 0)
                        comboAudioDriverVAC.SelectedIndex = 0;
                }
                else
                {
                    comboAudioDriverVAC.Items.Clear();
                    int host_index = 0;

                    foreach (string PAHostName in Audio.GetPAHosts())
                    {
                        if (Audio.GetPAInputdevices(host_index).Count > 0 ||
                            Audio.GetPAOutputdevices(host_index).Count > 0)
                        {
                            comboAudioDriverVAC.Items.Add(new PADeviceInfo(PAHostName, host_index));
                        }

                        host_index++; //Increment host index
                    }

                    if(comboAudioDriverVAC.Items.Count > 0)
                        comboAudioDriverVAC.SelectedIndex = 0;
                }
            }
            catch (Exception ex)
            {
                Debug.Write(ex.ToString());

                if (console.debug_enabled && !console.ConsoleClosing)
                    console.Invoke(new DebugCallbackFunction(console.DebugCallback),
                        "Audio get hosts error!\n" + ex.ToString());
            }
        }

		private void GetDevicesVAC()
		{
            try
            {
                comboAudioInputVAC.Items.Clear();
                comboAudioOutputVAC.Items.Clear();
                int host = ((PADeviceInfo)comboAudioDriverVAC.SelectedItem).Index;
                ArrayList a = Audio.GetPAInputdevices(host);
                foreach (PADeviceInfo p in a)
                    comboAudioInputVAC.Items.Add(p);

                a = Audio.GetPAOutputdevices(host);
                foreach (PADeviceInfo p in a)
                    comboAudioOutputVAC.Items.Add(p);

                if (comboAudioInputVAC.Items.Count > 0)
                    comboAudioInputVAC.SelectedIndex = 0;
                if (comboAudioOutputVAC.Items.Count > 0)
                    comboAudioOutputVAC.SelectedIndex = 0;
            }
            catch (Exception ex)
            {
                Debug.Write(ex.ToString());

                if (console.debug_enabled && !console.ConsoleClosing)
                    console.Invoke(new DebugCallbackFunction(console.DebugCallback),
                        "Audio get VAC devices error!\n" + ex.ToString());
            }
		}

		private void ControlList(Control c, ref ArrayList a)  // changes yt7pwr
		{
            try
            {
                if (c.Controls.Count > 0)
                {
                    foreach (Control c2 in c.Controls)
                        ControlList(c2, ref a);
                }

                if (c.GetType() == typeof(CheckBoxTS) || c.GetType() == typeof(CheckBox) ||
                    c.GetType() == typeof(ComboBoxTS) || c.GetType() == typeof(ComboBox) ||
                    c.GetType() == typeof(NumericUpDownTS) || c.GetType() == typeof(NumericUpDown) ||
                    c.GetType() == typeof(RadioButtonTS) || c.GetType() == typeof(RadioButton) ||
                    c.GetType() == typeof(TextBoxTS) || c.GetType() == typeof(TextBox) ||
                    c.GetType() == typeof(TrackBarTS) || c.GetType() == typeof(TrackBar) ||
                    c.GetType() == typeof(ColorButton) || c.GetType() == typeof(PrettyTrackBar))
                    a.Add(c);
            }
            catch (Exception ex)
            {
                Debug.Write(ex.ToString());

                if (console.debug_enabled && !console.ConsoleClosing)
                    console.Invoke(new DebugCallbackFunction(console.DebugCallback),
                        "Get control list error!\n" + ex.ToString());
            }
		}

		private static bool saving = false;

        public void SaveOptions()
        {
            // Automatically saves all control settings to the database in the tab
            // pages on this form of the following types: CheckBoxTS, ComboBoxTS,
            // NumericUpDown, RadioButtonTS, TextBox, and TrackBar (slider)

            try
            {
                bool pwr = console.chkPower.Checked;

                if (pwr)
                {
                    console.chkPower.Checked = false;
                    Thread.Sleep(100);
                }

                saving = true;

                ArrayList a = new ArrayList();
                ArrayList temp = new ArrayList();

                ControlList(this, ref temp);

                foreach (Control c in temp)				// For each control
                {
                    if (c.GetType() == typeof(CheckBoxTS))
                        a.Add(c.Name + "/" + ((CheckBoxTS)c).Checked.ToString());
                    else if (c.GetType() == typeof(ComboBoxTS))
                    {
                        //if(((ComboBoxTS)c).SelectedIndex >= 0)
                        a.Add(c.Name + "/" + ((ComboBoxTS)c).Text);
                    }
                    else if (c.GetType() == typeof(NumericUpDownTS))
                        a.Add(c.Name + "/" + ((NumericUpDownTS)c).Value.ToString());
                    else if (c.GetType() == typeof(RadioButtonTS))
                        a.Add(c.Name + "/" + ((RadioButtonTS)c).Checked.ToString());
                    else if (c.GetType() == typeof(TextBoxTS))
                        a.Add(c.Name + "/" + ((TextBoxTS)c).Text);
                    else if (c.GetType() == typeof(TrackBarTS))
                        a.Add(c.Name + "/" + ((TrackBarTS)c).Value.ToString());
                    else if (c.GetType() == typeof(ColorButton))
                    {
                        Color clr = ((ColorButton)c).Color;
                        a.Add(c.Name + "/" + clr.R + "." + clr.G + "." + clr.B + "." + clr.A);
                    }
#if(DEBUG)
                    else if (c.GetType() == typeof(GroupBox) ||
                        c.GetType() == typeof(CheckBox) ||
                        c.GetType() == typeof(ComboBox) ||
                        c.GetType() == typeof(NumericUpDown) ||
                        c.GetType() == typeof(RadioButton) ||
                        c.GetType() == typeof(TextBox) ||
                        c.GetType() == typeof(TrackBar))
                        Debug.WriteLine(c.Name + " needs to be converted to a Thread Safe control.");
                    Thread.Sleep(1);
#endif
                }

                a.Add("DisplayFont" + "/" + DisplayPanadapterFont.ToString() + "/" +
                    DisplayPanadapterFont.Size.ToString() + "/" + DisplayPanadapterFont.Style.ToString());
                a.Add("NewVFOBandFont" + "/" + console.NewVFOBandFont.ToString() + "/" +
                    console.NewVFOBandFont.Size.ToString() + "/" + console.NewVFOBandFont.Style.ToString());
                a.Add("NewVFOSmallFont" + "/" + console.NewVFOSmallFont.ToString() + "/" +
                    console.NewVFOSmallFont.Size.ToString() + "/" + console.NewVFOSmallFont.Style.ToString());
                a.Add("NewVFOLargeFont" + "/" + console.NewVFOLargeFont.ToString() + "/" +
                    console.NewVFOLargeFont.Size.ToString() + "/" + console.NewVFOLargeFont.Style.ToString());
                a.Add("VFOSmallFont" + "/" + console.VFOSmallFont.ToString() + "/" +
                    console.VFOSmallFont.Size.ToString() + "/" + console.VFOSmallFont.Style.ToString());
                a.Add("MainMenuFont" + "/" + console.MainMenuFont.ToString() + "/" +
                    console.MainMenuFont.Size.ToString() + "/" + console.MainMenuFont.Style.ToString());
                string s = "WaterfallLowLevel/";
                for (int i = 0; i <= (int)Band.BX12; i++)
                    s += waterfall_low_level[i].ToString("R") + "|";
                s = s.Substring(0, s.Length - 1);
                a.Add(s);
                s = "WaterfallHighLevel/";
                for (int i = 0; i <= (int)Band.BX12; i++)
                    s += waterfall_high_level[i].ToString("R") + "|";
                s = s.Substring(0, s.Length - 1);
                a.Add(s);
                s = "SpectrumLowLevel/";
                for (int i = 0; i <= (int)Band.BX12; i++)
                    s += spectrum_low_level[i].ToString("R") + "|";
                s = s.Substring(0, s.Length - 1);
                a.Add(s);
                s = "SpectrumHighLevel/";
                for (int i = 0; i <= (int)Band.BX12; i++)
                    s += spectrum_high_level[i].ToString("R") + "|";
                s = s.Substring(0, s.Length - 1);
                a.Add(s);

                a.Sort();
                DB.SaveVars("State", ref a);		// save the values to the DB
                saving = false;

                console.chkPower.Checked = pwr;
            }
            catch (Exception ex)
            {
                MessageBox.Show("Error in SaveOption function!\n" + ex.ToString());
            }
        }

        public void GetOptions()  // changes yt7pwr
        {
            // Automatically restores all controls from the database in the
            // tab pages on this form of the following types: CheckBoxTS, ComboBoxTS,
            // NumericUpDown, RadioButtonTS, TextBox, and TrackBar (slider)

            try
            {
                bool pwr = console.chkPower.Checked;

                for (int i = 0; i <= (int)Band.BX12; i++)
                {
                    waterfall_low_level[i] = -145.0f;
                    waterfall_high_level[i] = -70.0f;
                    spectrum_high_level[i] = -70.0f;
                    spectrum_low_level[i] = -145.0f;
                }

                if (pwr)
                {
                    console.chkPower.Checked = false;
                    Thread.Sleep(500);
                }

                // get list of live controls
                ArrayList temp = new ArrayList();		// list of all first level controls
                ControlList(this, ref temp);
                ArrayList checkbox_list = new ArrayList();
                ArrayList combobox_list = new ArrayList();
                ArrayList numericupdown_list = new ArrayList();
                ArrayList radiobutton_list = new ArrayList();
                ArrayList textbox_list = new ArrayList();
                ArrayList trackbar_list = new ArrayList();
                ArrayList colorbutton_list = new ArrayList();

                //ArrayList controls = new ArrayList();	// list of controls to restore
                foreach (Control c in temp)
                {
                    if (c.GetType() == typeof(CheckBoxTS))			// the control is a CheckBoxTS
                        checkbox_list.Add(c);
                    else if (c.GetType() == typeof(ComboBoxTS))		// the control is a ComboBoxTS
                        combobox_list.Add(c);
                    else if (c.GetType() == typeof(NumericUpDownTS))	// the control is a NumericUpDown
                        numericupdown_list.Add(c);
                    else if (c.GetType() == typeof(RadioButtonTS))	// the control is a RadioButtonTS
                        radiobutton_list.Add(c);
                    else if (c.GetType() == typeof(TextBoxTS))		// the control is a TextBox
                        textbox_list.Add(c);
                    else if (c.GetType() == typeof(TrackBarTS))		// the control is a TrackBar (slider)
                        trackbar_list.Add(c);
                    else if (c.GetType() == typeof(ColorButton))
                        colorbutton_list.Add(c);
                }

                ArrayList a = new ArrayList();
                a = DB.GetVars("State");							// Get the saved list of controls
                a.Sort();

                foreach (string s in a)				// string is in the format "name,value"
                {
                    string[] vals = s.Split('/');

                    if (vals.Length > 2)
                    {
                        for (int i = 2; i < vals.Length; i++)
                            vals[1] += "/" + vals[i];
                    }

                    string name = vals[0];
                    string val = vals[1];

                    switch (name)
                    {
                        case "tx_image_real_table":
                            string[] list = val.Split('|');
                            for (int i = 0; i <= (int)Band.BX12 && i < list.Length; i++)
                                console.tx_image_phase_table[i] = float.Parse(list[i]);
                            break;
                        case "tx_image_imag_table":
                            list = val.Split('|');
                            for (int i = 0; i <= (int)Band.BX12 && i < list.Length; i++)
                                console.tx_image_gain_table[i] = float.Parse(list[i]);
                            break;

                        case "WaterfallLowLevel":
                            list = val.Split('|');
                            for (int i = 0; i <= (int)Band.BX12 && i < list.Length; i++)
                            {
                                if (float.Parse(list[i]) > -200.0f && float.Parse(list[i]) < 200.0f)
                                    waterfall_low_level[i] = float.Parse(list[i]);
                                else
                                    waterfall_low_level[i] = -150.0f;
                            }
                            break;

                        case "WaterfallHighLevel":
                            list = val.Split('|');
                            for (int i = 0; i <= (int)Band.BX12 && i < list.Length; i++)
                            {
                                if (float.Parse(list[i]) > -200.0f && float.Parse(list[i]) < 200.0f)
                                    waterfall_high_level[i] = float.Parse(list[i]);
                                else
                                    waterfall_high_level[i] = -50.0f;
                            }
                            break;

                        case "SpectrumLowLevel":
                            list = val.Split('|');
                            for (int i = 0; i <= (int)Band.BX12 && i < list.Length; i++)
                            {
                                if (float.Parse(list[i]) > -200.0f && float.Parse(list[i]) < 200.0f)
                                    spectrum_low_level[i] = float.Parse(list[i]);
                                else
                                    spectrum_low_level[i] = -150.0f;
                            }
                            break;

                        case "SpectrumHighLevel":
                            list = val.Split('|');
                            for (int i = 0; i <= (int)Band.BX12 && i < list.Length; i++)
                            {
                                if (float.Parse(list[i]) > -200.0f && float.Parse(list[i]) < 200.0f)
                                    spectrum_high_level[i] = float.Parse(list[i]);
                                else
                                    spectrum_high_level[i] = -50.0f;
                            }
                            break;

                        default:

                            if (s.StartsWith("chk") || s.StartsWith("check"))			// control is a CheckBoxTS
                            {
                                for (int i = 0; i < checkbox_list.Count; i++)
                                {	// look through each control to find the matching name
                                    CheckBoxTS c = (CheckBoxTS)checkbox_list[i];
                                    if (c.Name.Equals(name))		// name found
                                    {
                                        c.Checked = bool.Parse(val);	// restore value
                                        i = checkbox_list.Count + 1;
                                    }
                                    if (i == checkbox_list.Count)
                                        MessageBox.Show("Control not found: " + name);
                                }
                            }
                            else if (s.StartsWith("combo"))	// control is a ComboBoxTS
                            {
                                for (int i = 0; i < combobox_list.Count; i++)
                                {	// look through each control to find the matching name
                                    ComboBoxTS c = (ComboBoxTS)combobox_list[i];
                                    if (c.Name.Equals(name))		// name found
                                    {
                                        if (c.Items.Count > 0 && c.Items[0].GetType() == typeof(string))
                                        {
                                            c.Text = val;
                                        }
                                        else
                                        {
                                            foreach (object o in c.Items)
                                            {
                                                if (o.ToString() == val)
                                                    c.Text = val;	// restore value
                                            }
                                        }
                                        i = combobox_list.Count + 1;
                                    }
                                    if (i == combobox_list.Count)
                                        MessageBox.Show("Control not found: " + name);
                                }
                            }
                            else if (s.StartsWith("ud"))
                            {
                                for (int i = 0; i < numericupdown_list.Count; i++)
                                {	// look through each control to find the matching name
                                    NumericUpDownTS c = (NumericUpDownTS)numericupdown_list[i];
                                    if (c.Name.Equals(name))		// name found
                                    {
                                        decimal num = decimal.Parse(val);

                                        if (num > c.Maximum)
                                            num = c.Maximum;		// check endpoints
                                        else if (num < c.Minimum)
                                            num = c.Minimum;

                                        c.Value = num;			// restore value
                                        i = numericupdown_list.Count + 1;
                                    }
                                    if (i == numericupdown_list.Count)
                                        MessageBox.Show("Control not found: " + name);
                                }
                            }
                            else if (s.StartsWith("rad"))
                            {	// look through each control to find the matching name
                                for (int i = 0; i < radiobutton_list.Count; i++)
                                {
                                    RadioButtonTS c = (RadioButtonTS)radiobutton_list[i];
                                    if (c.Name.Equals(name))		// name found
                                    {
                                        c.Checked = bool.Parse(val);	// restore value
                                        i = radiobutton_list.Count + 1;
                                    }
                                    if (i == radiobutton_list.Count)
