                                }
                            }
                            else if (s.StartsWith("txt"))
                            {	// look through each control to find the matching name
                                for (int i = 0; i < textbox_list.Count; i++)
                                {
                                    TextBoxTS c = (TextBoxTS)textbox_list[i];
