                                        MessageBox.Show("Control not found: " + name);
                                }
                            }
                            else if (s.StartsWith("tb"))
                            {
                                // look through each control to find the matching name
                                for (int i = 0; i < trackbar_list.Count; i++)
                                {
                                    TrackBarTS c = (TrackBarTS)trackbar_list[i];
