		private string KeyToString(Keys k)
		{
			if(!k.ToString().StartsWith("Oem"))
				return k.ToString();

			string s = "";
			switch(k)
			{
				case Keys.OemOpenBrackets:
					s = "[";
					break;
				case Keys.OemCloseBrackets:
					s = "]";
					break;
				case Keys.OemQuestion:
					s = "/";
					break;
				case Keys.OemPeriod:
					s = ".";
					break;
				case Keys.OemPipe:
					if((k & Keys.Shift) == 0)
						s = "\\";
					else s = "|";
					break;
			}
			return s;
		}

