        private void comboSMeterRXMode_SelectedIndexChanged(object sender, EventArgs e)
        {
            if (comboSMeterRXMode.Items.Count == 0 ||
                comboSMeterRXMode.SelectedIndex < 0)
            {
                console.CurrentMeterRXMode = MeterRXMode.FIRST;
            }
            else
            {
                MeterRXMode mode = MeterRXMode.FIRST;
                switch (comboSMeterRXMode.Text)
                {
                    case "Signal":
                        mode = MeterRXMode.SIGNAL_STRENGTH;
                        break;
                    case "Sig Avg":
                        console.multimeter_avg = Display_GDI.CLEAR_FLAG;
                        mode = MeterRXMode.SIGNAL_AVERAGE;
                        break;
                    case "ADC L":
                        mode = MeterRXMode.ADC_L;
                        break;
                    case "ADC R":
                        mode = MeterRXMode.ADC_R;
                        break;
                    case "Off":
                        mode = MeterRXMode.OFF;
                        break;
                }

                console.CurrentMeterRXMode = mode;
                console.toolStripNewVFOSmeterRX.Text = comboSMeterRXMode.Text;
            }
        }

